INSERT INTO `wp_posts` VALUES (51, 1, '2017-01-20 17:51:19', '2017-01-20 17:51:19', '<span style=\"font-size: 14pt;\"><strong>Huongdanviendulich.vn</strong> </span>là trang chia sẻ của cộng đồng hướng dẫn viên du lịch uy tín nhất Việt Nam.\r\n<h2>Tầm nhìn và sứ mệnh</h2>\r\n- Tập hợp các hướng dẫn viên trên toàn quốc\r\n\r\n- Xây dựng hệ thống hành chính, tạo điều kiện thuận lợi cho việc hoạt động và sự phát triển của hướng dẫn viên\r\n\r\n- Xậy dựng hệ thống thông tin, hệ thống dẫn tour toàn diện, hiện đại\r\n\r\n- Tạo sân chơi lành mạnh, thân thiện cho cộng đồng hướng dẫn viên du lịch Việt Nam\r\n\r\n- Xây dựng hệ thống Review, phản hồi của khách hàng của các Công ty lữ hành\r\n\r\n- Thu hút và hỗ trợ đào tạo nhân tài\r\n\r\n- Thu thấp thông tin, phát triển tối đa năng lực thành viên\r\n\r\n- Đưa hướng dẫn viên Du lịch Việt Nam vươn tầm quốc tế\r\n<blockquote>&nbsp;\r\n<p style=\"text-align: center;\"><span style=\"color: #ff0000; font-size: 14pt;\">Bấm \"liên hệ\" để cho chúng tôi biết khó khăn của các bạn, chúng ta cùng nhau xây dựng cách thức giải quyết.</span></p>\r\n</blockquote>\r\n&nbsp;\r\n\r\n&nbsp;', 'Giới thiệu', '', 'publish', 'closed', 'closed', '', 'gioi-thieu', '', '', '2019-11-13 23:49:44', '2019-11-13 16:49:44', '', 0, 'http://localhost/wp/reviewskin/?page_id=51', 0, 'page', '', 0);
